function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (g_form.getBooleanValue('return')) {
        g_form.setSectionDisplay('supporting_details', true);
        g_form.setDisplay('return_date', true);
        g_form.setMandatory('return_date', true);

    } else {
        g_form.setSectionDisplay('supporting_details', false);
        g_form.setMandatory('return_date', false);
        g_form.clearValue('return_date');
        g_form.setDisplay('return_date', false);

    }

   // g_form.setValue('u_integer_4', parseInt(g_form.getValue('u_integer_3')) + parseInt(g_form.getValue('u_integer_2')) + parseInt(g_form.getValue('u_integer_1')));

     g_form.setValue('u_integer_4', sum(parseInt(g_form.getValue('u_integer_3')), parseInt(g_form.getValue('u_integer_2')), parseInt(g_form.getValue('u_integer_1'))));
   // var calc = x_136826_booking_a.calculate.calculateAllC(parseInt(g_form.getValue('u_integer_3')), parseInt(g_form.getValue('u_integer_2')), parseInt(g_form.getValue('u_integer_1')));

   // getUIScript['x_136826_booking_a.calculate'].calculateAllC(parseInt(g_form.getValue('u_integer_3')), parseInt(g_form.getValue('u_integer_2')), parseInt(g_form.getValue('u_integer_1')));


    //alert(calc);
    /*var f_sec = g_form.getSectionNames();
    for (i=0;i<f_sec.length;i++){
    	alert (f_sec[i]);
    }*/

}
